        // é€šè¿‡æ»‘åŠ¨çª—å£çš„æ–¹å¼æˆªå–é•¿åº¦ä¸º wordLen * wordsCount çš„å­ä¸²
        for (j = i; j &amp;lt; i + all_len; j += one_word_len) &amp;#123;
            strncpy(temp, s + j, one_word_len);
            temp[one_word_len] = &#39;\0&#39;; // æ³¨æ„ï¼šå°†å­—ç¬¦æ•°ç»„æœ«å°¾è®¾ç½®ä¸º &#39;\0&#39;

            res = 0; // æ ‡è®°è¯¥å•è¯åœ¨ words ä¸­æ˜¯å¦è¢«æŸ¥åˆ°
            for (k = 0; k &amp;lt; wordsSize; k++) &amp;#123;
                if (flag[k] == 1) continue;
                if (strcmp(temp, words[k]) == 0) &amp;#123;
                    flag[k] = 1;
                    res = 1;
                    break;//æ‰¾åˆ°äº†
                &amp;#125;
            &amp;#125;
-.3
