int romanToInt(char * s)&amp;#123;
    int nums[128];
    nums[&#39;I&#39;] = 1;
    nums[&#39;V&#39;] = 5;
    nums[&#39;X&#39;] = 10;
    nums[&#39;L&#39;] = 50;
    nums[&#39;C&#39;] = 100;
    nums[&#39;D&#39;] = 500;
    nums[&#39;M&#39;] = 1000;

    int sum = 0;
    while(*s)&amp;#123;
        if(nums[*s] &amp;lt; nums[*(s+1)]) sum -= nums[*s];
        else sum += nums[*s];
        s++;
    &amp;#125;//è®¡ç®—è§„åˆ™
    return sum;
&amp;#125;

int main() &amp;#123;
    char s[] = &amp;quot;MCMXCIV&amp;quot;;
    printf(&amp;quot;%d\n&amp;quot;, romanToInt(s));
    return 0;
